import React from "react";

function Card({ person }) {
  return (
    // <div className="tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5">
    //   <div>
    //     <h2>{person.title}</h2>
    //     <p>{person.date}</p>
    //   </div>
    // </div>
    <div className="flex flex-col lg:flex-row w-full items-start lg:items-center rounded bg-white shadow hover:shadow-xl z-10">
      <div className="w-full lg:w-1/3 h-5 dark:border-gray-700 lg:h-20 border-t lg:border-t-0 lg:border-r lg:border-l lg:rounded-r dark:bg-gray-700 bg-gray-100 hover:shadow">
        <div className="text-center pt-3 h-1/2  p-2">{person.date}</div>
        <div className="text-center text-red-500">{person.status}</div>
      </div>
      <text className="p-2 truncate  text-lg text-center bottom-10 justify-center w-full lg:w-2/3 h-10 dark:bg-gray-1000 font-semibold ">
        {person.title}
      </text>
    </div>
  );
}

export default Card;
