import React, { useEffect, useState, useCallback } from "react";
import { render } from "react-dom";
import { useSelector, useDispatch } from "react-redux";
import axios from "axios";
import store from "../store";
import Loading from "./loading";
export default function ListTailwind({ content }) {
  const { dispatch } = store;
  //   const selected = useSelector((state) => state.events.selected);

  const [selected, setSelected] = useState();
  const [isloading, setLoading] = useState(false);
  const [isModal, setMIsModal] = useState();
  const [mid, setMID] = useState();
  const [mtitle, setMTitle] = useState();
  const [mdate, setMDate] = useState();
  const [mstatus, setMStatus] = useState();

  const selectHandler = useCallback(async (params) => {
    dispatch({ type: "events/setSelected", payload: params });
    console.log("meron ba", params);
    setSelected(params);
  });
  const refreshList = useCallback(async (isRefresh = false) => {
    dispatch({ type: "events/list" });
  });

  const dateStr = "2021-23-2";
  // console.log(dateStr);

  const [year, month, day] = dateStr.split("-");
  // console.log("hinanda", [day, month, year]);
  let newDate = `${year}-${month}-${day}`;
  const UpdateDataHandler = useCallback(async (isRefresh = false) => {
    try {
      //   setLoading(true);
      axios
        .put("http://localhost:8000/events/" + mid + "/", {
          title: mtitle,
          status: mstatus,
          date: mdate,
        })
        .then((res) => {
          //   setLoading(false);
          refreshList();
          console.log(res.data);
        });
      setMIsModal(false);
      //   refreshList();
    } catch (error) {
      console.log("nasend na may error");
      console.warn(error);
      //   setStatus("Completed");
    }
  });

  const DeleteDataHandler = useCallback(async (isRefresh = false) => {
    try {
      //   setLoading(true);
      axios
        .delete("http://localhost:8000/events/" + mid + "/", {
          title: mtitle,
          status: mstatus,
          date: mdate,
        })
        .then((res) => {
          //   setLoading(false);
          refreshList();
          console.log(res.data);
        });
      setMIsModal(false);
      //   refreshList();
    } catch (error) {
      console.log("nasend na may error");
      console.warn(error);
      //   setStatus("Completed");
    }
  });

  if (isloading) {
    return <Loading />;
  }

  return (
    <div>
      {!isModal && (
        <ul className="mt-10 ">
          {content.map((item, i) => (
            <>
              <div
                className="flex  justify-self-auto m-auto mt-4  w-1/4"
                // onClick={() => {
                //   //   console.log("napindot", [i, item.date, item.status, item.title])
                //   //   console.log("napindot", item.date.length)
                //   const dateStr = "2021/12/02";
                //   const formatDate = (dateStr) => {
                //     const [year, month, day] = dateStr.split("/");
                //     let newDate = `${month}/${day}/${year}`;

                //     console.log(newDate);
                //   };

                //   formatDate(dateStr);
                // }}
                // onClick={() => {
                //   console.log("item", item);
                // }}
                //   onClick={selectHandler(item)}
                onClick={() => {
                  selectHandler(item);
                  console.log(selected);
                  setMIsModal(true);
                  setMID(item.id);
                  setMTitle(item.title);
                  setMDate(item.date);
                  setMStatus(item.status);
                }}
              >
                <div className="flex flex-col lg:flex-row w-full items-start lg:items-center rounded bg-white shadow hover:shadow-2xl z-10">
                  <div className="w-full lg:w-1/3 h-5 dark:border-gray-700 lg:h-20 border-t lg:border-t-0 lg:border-r lg:border-l lg:rounded-r dark:bg-gray-700 bg-gray-100 hover:shadow">
                    <div className="text-center pt-3 h-1/2  p-2">
                      {item.date}
                    </div>
                    <div className="text-center text-red-500">
                      {item.status}
                    </div>
                  </div>
                  <text className="p-2 truncate  text-lg text-center bottom-10 justify-center w-full lg:w-2/3 h-10 dark:bg-gray-1000 font-semibold ">
                    {item.title}
                  </text>
                </div>
              </div>
            </>
          ))}
        </ul>
      )}
      {isModal && (
        <div>
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div> Update Events</div>
            <div className="flex truncate flex-col justify-center items-center px-4 py-3">
              <input
                //   defaultValue={title}
                value={mtitle || ""}
                id="title"
                type="text"
                className="form-input flex m-1 items-center justify-center w-3/4 border-blue-200 rounded"
                placeholder="Title"
                onChange={(e) => setMTitle(e.target.value)}
              />
              <input
                defaultValue={newDate}
                value={mdate || ""}
                id="date"
                type="date"
                className="form-input m-1 truncate content-center border-blue-200  w-3/4 rounded"
                onChange={(e) => setMDate(e.target.value)}
              />

              {mdate.length > 10 && mdate.length > 1 && (
                <text className="relative right-10 text-red-500 text-sm">
                  incorrect date format
                </text>
              )}
              <select
                // defaultValue={mstatus}
                // value={mstatus}
                className=" m-1 content-center truncate border-blue-200  w-3/4 rounded "
                onChange={(e) => setMStatus(e.target.value)}
              >
                <option>Choose Status</option>
                <option>Pending</option>
                <option>Done</option>
              </select>
              {/* {hasError && (
            <text className="relative right-10 text-red-500 text-sm">
              Please Complete Details
            </text>
          )} */}

              <button
                //   disabled={hasError}
                id="ok-btn"
                className="px-4 py-2 mt-3 bg-blue-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-grey-600 bg-grey-600) focus:outline-none focus:ring-2 focus:ring-blue-700"
                //   className={
                //     !hasError
                //       ? "px-4 py-2 mt-3 bg-blue-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-grey-600 bg-grey-600) focus:outline-none focus:ring-2 focus:ring-blue-700"
                //       : "px-4 py-2 mt-3 bg-gray-300 text-gray-400 text-base font-medium rounded-md w-full shadow-sm ) focus:outline-none  "
                //   }
                onClick={UpdateDataHandler}
                //   onClick={() => {
                //     console.log("wow", status);
                //   }}
              >
                Update
              </button>
              <button
                //   disabled={hasError}
                id="ok-btn"
                className="px-4 py-2 mt-3 bg-blue-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-grey-600 bg-grey-600) focus:outline-none focus:ring-2 focus:ring-blue-700"
                //   className={
                //     !hasError
                //       ? "px-4 py-2 mt-3 bg-blue-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-grey-600 bg-grey-600) focus:outline-none focus:ring-2 focus:ring-blue-700"
                //       : "px-4 py-2 mt-3 bg-gray-300 text-gray-400 text-base font-medium rounded-md w-full shadow-sm ) focus:outline-none  "
                //   }
                onClick={() => {
                  setMIsModal(false);
                }}
